import styled from 'styled-components';

export const ContactPageContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
`;

export const IconContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
`

export const Icon = styled.i`
    color: white;
    background-color: #323338;
    border-radius: 50%;
    padding: 15px 20px;
    margin: 20px;
    font-size: 30px;
`
